import React, { useState } from 'react';
import '../css/ImageToArticle.css';

const ImageToArticle = () => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [jsonFileData, setJsonFileData] = useState(null);
  const [apiKey, setApiKey] = useState('');
  const [fileName, setFileName] = useState('');

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    setFileName(file?.name || '');
    const reader = new FileReader();
    reader.onload = (event) => {
      try {
        const json = JSON.parse(event.target.result);
        setJsonFileData(json);
      } catch (error) {
        alert('Invalid JSON file.');
        setJsonFileData(null);
      }
    };
    if (file) reader.readAsText(file);
  };

  const handleGenerateArticle = async () => {
    if (!jsonFileData || !apiKey) return;

    setLoading(true);
    try {
      const message = `Write a short article based on the following image labels:\n\n${JSON.stringify(jsonFileData, null, 2)}`;

      const res = await fetch('/api/article', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${apiKey}`
        },
        body: JSON.stringify({ message }),
      });

      const data = await res.json();

      const newArticle = {
        title: data.title || "Article Title Here",
        description: data.content || "Something poetic was meant to be here.",
        category: "Sports",
        date: new Date().toLocaleDateString(),
        imageUrl: "./images/basketball.png"
      };

      setArticles((prev) => [newArticle, ...prev]);
    } catch (err) {
      console.error('Error generating article:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container py-5">
      <div className="d-flex flex-column align-items-center mb-4">
        <img
          src="./images/basketball.png"
          alt="Basketball"
          className="mb-3"
          style={{ width: '500px', height: 'auto' }}
        />
        <div className="mb-3 d-flex gap-2 align-items-center">
          <input
            type="file"
            accept=".json"
            id="fileInput"
            hidden
            onChange={handleFileUpload}
          />
          <button
            className="btn btn-secondary"
            onClick={() => document.getElementById('fileInput').click()}
          >
            Upload JSON File
          </button>
          <span className="text-muted small">{fileName}</span>
        </div>
        <div className="mb-3 w-100" style={{ maxWidth: '500px' }}>
          <input
            type="text"
            className="form-control"
            placeholder="Enter your API key"
            value={apiKey}
            onChange={(e) => setApiKey(e.target.value)}
          />
        </div>
        <button
          className="btn btn-primary"
          onClick={handleGenerateArticle}
          disabled={loading || !jsonFileData || !apiKey}
        >
          {loading ? 'Generating...' : 'Generate Article'}
        </button>
      </div>

      <div className="row g-3">
        <div className={`articles-wrapper ${loading ? 'shifted' : ''}`}>
          {loading && (
            <div className="col-md-4">
              <div className="card p-3 shadow-sm h-100">
                <div className="skeleton skeleton-card-img mb-2" />
                <div className="skeleton skeleton-title mb-2" />
                <div className="skeleton skeleton-text mb-1" />
                <div className="skeleton skeleton-text mb-2" />
                <div className="skeleton skeleton-link" />
              </div>
            </div>
          )}

          {articles.map((article, index) => (
            <div key={index} className="col-md-3">
              <div className="card p-3 shadow-sm h-100">
                <span
                  className={`badge mb-2 ${article.category === 'Food' ? 'bg-warning text-dark' : article.category === 'Travel' ? 'bg-success' : 'bg-primary'}`}
                >
                  {article.category}
                </span>
                <img src={article.imageUrl} className="card-img-top mb-2 rounded" alt={article.title} />
                <h6>{article.title}</h6>
                <p className="text-muted small">{article.date}</p>
                <p className="small">{article.description}</p>
                <a href="#" className="text-primary small">
                  Read more <i className="fas fa-arrow-right"></i>
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ImageToArticle;
