import React, { useState } from 'react';
import '../../css/ImageToArticle.css'; // Assuming you have a custom CSS file for additional styling.

const ImageToArticle = () => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(false);

  const jsonData = {
    "Labels": [
      { "Name": "Ball", "Confidence": 99.66, "Categories": [{ "Name": "Sports" }] },
      { "Name": "Basketball", "Confidence": 99.66, "Parents": [{ "Name": "Sport" }], "Categories": [{ "Name": "Sports" }] },
      { "Name": "Basketball (Ball)", "Confidence": 99.66, "Parents": [{ "Name": "Ball" }, { "Name": "Basketball" }, { "Name": "Sport" }], "Categories": [{ "Name": "Sports" }] },
      { "Name": "Sport", "Confidence": 99.66, "Aliases": [{ "Name": "Sports" }], "Categories": [{ "Name": "Sports" }] },
      { "Name": "Person", "Confidence": 98.97, "Categories": [{ "Name": "Person Description" }] },
      { "Name": "Playing Basketball", "Confidence": 98.97, "Parents": [{ "Name": "Basketball" }, { "Name": "Person" }, { "Name": "Sport" }], "Categories": [{ "Name": "Actions" }] },
      { "Name": "Basketball Game", "Confidence": 98.24, "Parents": [{ "Name": "Basketball" }, { "Name": "Sport" }], "Categories": [{ "Name": "Sports" }] },
      { "Name": "Adult", "Confidence": 95.89, "Parents": [{ "Name": "Person" }], "Categories": [{ "Name": "Person Description" }] },
      { "Name": "Male", "Confidence": 95.89 }
    ]
  };

  const handleGenerateArticle = async () => {
    setLoading(true);
    try {
      const message = `Write a short article based on the following image labels:\n\n${JSON.stringify(jsonData, null, 2)}`;
      
      const res = await fetch('/api/article', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message }),
      });

      const data = await res.json();

      console.log(data);
      const newArticle = {
        title: data.title || "Article Title Here",
        description: data.content || "Something poetic was meant to be here.",
        category: "Sports",
        date: new Date().toLocaleDateString(),
        imageUrl: "./images/basketball.png" // placeholder image
      };

      setArticles((prev) => [newArticle, ...prev]);
    } catch (err) {
      console.error('Error generating article:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container py-5">
      <div className="d-flex flex-column align-items-center mb-4">
        <img
            src="./images/basketball.png"
            alt="Basketball"
            className="mb-3"
            style={{ width: '500px', height: 'auto' }}
        />
        <button
            className="btn btn-primary"
            onClick={handleGenerateArticle}
            disabled={loading}
        >
            {loading ? 'Generating...' : 'Generate Article'}
        </button>
      </div>

      <div className="row g-3">
        <div className={`articles-wrapper ${loading ? 'shifted' : ''}`}>
          {/* Skeleton Loader (on left side) */}
          {loading && (
            <div className="col-md-4">
              <div className="card p-3 shadow-sm h-100">
                <div className="skeleton skeleton-card-img mb-2" />
                <div className="skeleton skeleton-title mb-2" />
                <div className="skeleton skeleton-text mb-1" />
                <div className="skeleton skeleton-text mb-2" />
                <div className="skeleton skeleton-link" />
              </div>
            </div>
          )}

          {/* Display Articles */}
          {articles.map((article, index) => (
            <div key={index} className="col-md-3">
              <div className="card p-3 shadow-sm h-100">
                <span
                  className={`badge mb-2 ${article.category === 'Food' ? 'bg-warning text-dark' : article.category === 'Travel' ? 'bg-success' : 'bg-primary'}`}
                >
                  {article.category}
                </span>
                <img src={article.imageUrl} className="card-img-top mb-2 rounded" alt={article.title} />
                <h6>{article.title}</h6>
                <p className="text-muted small">{article.date}</p>
                <p className="small">{article.description}</p>
                <a href="#" className="text-primary small">
                  Read more <i className="fas fa-arrow-right"></i>
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ImageToArticle;
